import React from 'react';
import { getNews } from '../../dev/get-news'
import './news.css';
import { DefaultValues } from '../../store/types'

type Props = {
  defaultValues: DefaultValues
}
type State = {
  showNews: boolean
}

export class News extends React.PureComponent<Props, State> {
  public state: State = {
    showNews: false

  }
  public render(): React.ReactElement {
    const { googleAccesKey, sid, searchRequest, newsAmount } = this.props.defaultValues

    // getNews(googleAccesKey, sid, searchRequest).forEach((promise: Promise<any>) => {
    //   promise.then(response => {
    //     console.log(response)
    //   })
    // })
    // getNews(googleAccesKey, sid, searchRequest, newsAmount)

    // const newB = this.getNewsElements()
    // console.log(newB)
    return (
      <div className="root">
        <button onClick={this.getNews}></button>
        {this.state.showNews && this.getNewsElements()}
      </div>
    )
  }


  private getNews = (): any => {
    this.setState({
      showNews: true
    })
  }


  private getNewsElements = (): any => {
    const { googleAccesKey, sid, searchRequest, newsAmount } = this.props.defaultValues
    const news = getNews(googleAccesKey, sid, searchRequest, newsAmount)
    console.log(news)


    news.then((data: any) => {
      return data.items.map((item: any, index: number) => {
        return (<div key={index}>
          {item}
        </div>
        )
      })
    })
  }
}
