import React from 'react';
import { getNews } from '../../dev/get-news'
import './news.css';
import { DefaultValues } from '../../store/types'

type Props = {
  defaultValues: DefaultValues
}
type State = {
  showNews: boolean
  newsData: any
}

export class News extends React.PureComponent<Props, State> {
  public state: State = {
    showNews: false,
    newsData: {}
  }
  public render(): React.ReactElement {
    const { googleAccesKey, sid, searchRequest, newsAmount } = this.props.defaultValues
    return (
      <div className="root">
        <button onClick={this.getNews}></button>
        {this.state.showNews && this.getNewsElements()}
      </div>
    )
  }


  private getNews = (): any => {
    this.setState({
      showNews: true
    })
  }


  private getNewsElements = (): any => {
    const { googleAccesKey, sid, searchRequest } = this.props.defaultValues
    const news = getNews(googleAccesKey, sid, searchRequest)
    news.then((data: any) => {
      this.setState({
        newsData: { ...this.state.newsData, 'news': data.items }
      })
    })
    console.log(this.state.newsData)
  }
}
