import React from 'react';
import { config } from '../../store/config';
import { News } from '../news/news'
import './app.css';
import { Config } from '../../store/types';

type State = {}

export class App extends React.PureComponent<{}, State> {

  public render(): React.ReactElement {
    return (
      <div className="root">
        news
        <News
          defaultValues={config.defaultValues}
        />
        <button className="btn" onClick={this.getNews}>
          another one
        </button>
      </div>
    )
  }



  private getNews = (): any => {
    this.setState({
      showNews: true
    })
    this.getNewsElements()
  }

  private getNewsElements = (): any => {
    const { googleAccesKey, sid, searchRequest } = this.props.defaultValues
    const news = getNews(googleAccesKey, sid, searchRequest)
    news.then((data: any) => {
      this.setState({
        newsData: { ...this.state.newsData, news: data.items }
      })
    })
  }


}

export default App
