import React from 'react';
import { getNews } from '../../dev/get-news'
import './news.css';
import { DefaultValues } from '../../store/types'

type Props = {
  defaultValues: DefaultValues
}
type State = {}

export class News extends React.PureComponent<Props, State> {
  public render(): React.ReactElement {
    const { googleAccesKey, sid, searchRequest } = this.props.defaultValues

    // getNews(googleAccesKey, sid, searchRequest).forEach((promise: Promise<any>) => {
    //   promise.then(response => {
    //     console.log(response)
    //   })
    // })
    const newB = this.getNewsElements()
    console.log(newB)
    return (
      <div className="root">
        {this.getNewsElements()}

      </div>
    )
  }

  private getNewsElements = (): any => {
    const { googleAccesKey, sid, searchRequest } = this.props.defaultValues
    let newsCounter = 5

    for (let i = 0; i < newsCounter; i++) {
      // do {
      getNews(googleAccesKey, sid, searchRequest)
        .then((data: any) => {
          return data.items.map((item: any) => {
            newsCounter++
            return (<div>
              {item}
            </div>
            )
          })
        })
    }
    // }
    // while (newsCounter < 5)
  }
}
