import React from 'react';
import { getNews } from '../../dev/get-news'
import './news.css';
import { DefaultValues } from '../../store/types'

type Props = {
  defaultValues: DefaultValues
}
type State = {
  showNews: boolean
  newsData: any
}

type NewsDataset = {
  title: string
  htmlTitle: string
  formattedUrl: string
  snippet: string
  imageobject: { [key: string]: string }[]
}

export class News extends React.PureComponent<Props, State> {
  public state: State = {
    showNews: false,
    newsData: {}
  }
  public render(): React.ReactElement {

    return (
      <div className="root">
        <button className="btn" onClick={this.getNews}>
          another one
        </button>
        {this.state.showNews && this.state.newsData.news && this.renderElements()}
      </div>
    )
  }


  private getNews = (): any => {
    this.setState({
      showNews: true
    })
    this.getNewsElements()
  }

  private getNewsElements = (): any => {
    const { googleAccesKey, sid, searchRequest } = this.props.defaultValues
    const news = getNews(googleAccesKey, sid, searchRequest)
    news.then((data: any) => {
      this.setState({
        newsData: { ...this.state.newsData, news: data.items }
      })
    })
  }

  private renderElements = (): React.ReactElement[] => {
    return this.state.newsData.news.map((item: NewsDataset) => {
      return (
        <div>
          <div>{item.title}</div>
          <div>{item.snippet}</div>
        </div>
      )
    })
  }

}
